import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";




export default function ParticlesComponent(){
  const particlesInit = useCallback(async engine => {
    console.log(engine);
      await loadSlim(engine);
}, []);

const particlesLoaded = useCallback(async container => {
    await console.log(container);
}, []);
  return( <>
      <div className="w-full h-screen relative">
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          fullScreen: {
            enable: false,
            zIndex: 1,
          },
          particles: {
            number: {
              value: 80, // Augmentez ou diminuez le nombre de particules selon vos préférences
              density: {
                enable: true,
                value_area: 800,
              },
            },
            color: {
              value: "#ffffff", // Couleur des particules
            },
            shape: {
              type: "circle", // Type de forme des particules (circle, edge, triangle, polygon, star, etc.)
            },
            opacity: {
              value: 0.8,
              random: false,
            },
            size: {
              value: 10, // Taille des particules
              random: true,
            },
            move: {
              enable: true,
              speed: 3, // Vitesse de déplacement des particules
              direction: "none",
              random: true,
              straight: false,
              out_mode: "out",
              bounce: false,
            },
            line_linked: {
              enable: true,
              distance: 150, // Distance de connexion entre les particules
              color: "#ffffff",
              opacity: 0.4,
              width: 1,
            },
          },
          interactivity: {
            events: {
              onhover: {
                enable: true,
                mode: "repulse", // Effet de repoussement au survol de la souris
              },
            },
          },
          retina_detect: true,
        }}
      />
      <div className="absolute top-0 left-0 w-full h-full flex flex-col justify-center items-center text-center z-10">
        <div className="text-gray-500 text-lg font-bold">Gardez un œil sur vos commandes en temps réel, où que vous soyez et quand vous le souhaitez.</div>
        <img className="bg-cover w-full" src={require('./image/Design sans titre.png')} alt="" />
      </div>
    </div>
  </>
  )
};
