import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";




export default function ParticlesComponent(){
  const particlesInit = useCallback(async engine => {
    console.log(engine);
      await loadSlim(engine);
}, []);

const particlesLoaded = useCallback(async container => {
    await console.log(container);
}, []);
  return( <>
    <div className="w-full h-screen particles-container gradient">
    <div className="w-full h-screen  bg-black flex relative justify-end">
    <div ><h2 className="text-white w-24">
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Repudiandae amet labore ut. Consequatur sunt corrupti alias, reiciendis enim explicabo necessitatibus modi ipsam dolore eveniet vel sed inventore laboriosam magni harum?
     </h2> 
     </div>
      <div>
        {/* Replace './image/Design sans titre(1).svg' with the path to your image */}
        <img  className="w-full h-screen absolute" src={require('./image/Design sans titre.png')} alt="" />
      </div>

      </div>
      <Particles

        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          fullScreen: {
            enable: false,
            zIndex: 0,
          },
          particles: {
            number: {
              value: 14,
              density: {
                enable: false,
                value_area: 800,
              },
            },
            color: {
              value: "#fff",
            },
            shape: {
              type: "image",
              image: {
                src:
                  "https://www.colisprive.fr/wp-content/uploads/2018/05/logo-colis-prive-1.svg",
                width: 100,
                height: 100,
              },
            },
            opacity: {
              value: 0.8,
              random: false,
              anim: {
                enable: false,
                speed: 1,
                opacity_min: 0.1,
                sync: false,
              },
            },
            size: {
              value: { min: 10, max: 30 },
            },
            rotate: {
              value: 0,
              random: true,
              direction: "clockwise",
              animation: {
                enable: true,
                speed: 5,
                sync: false,
              },
            },
            line_linked: {
              enable: true,
              distance: 600,
              color: "#ffffff",
              opacity: 0.4,
              width: 2,
            },
            move: {
              enable: true,
              speed: 2,
              direction: "none",
              random: false,
              straight: false,
              out_mode: "out",
              attract: {
                enable: false,
                rotateX: 600,
                rotateY: 1200,
              },
            },
          },
          interactivity: {
            events: {
              onhover: {
                enable: true,
                mode: ["grab"],
              },
              onclick: {
                enable: false,
                mode: "bubble",
              },
              resize: true,
            },
            modes: {
              grab: {
                distance: 400,
                line_linked: {
                  opacity: 1,
                },
              },
              bubble: {
                distance: 400,
                size: 40,
                duration: 2,
                opacity: 8,
                speed: 3,
              },
              repulse: {
                distance: 200,
              },
              push: {
                particles_nb: 4,
              },
              remove: {
                particles_nb: 2,
              },
            },
          },
          retina_detect: true,
        }}
      />

    </div>
  </>
  )
};
